+    private static final String FORMAT = "%s-%s-%d";
+
+    private AtomicInteger threadNum = new AtomicInteger(1);
+    private String name;
+    private OptionalInt priority;
+
+    private NamedThreadFactory(String name) {
+      this(name, OptionalInt.empty());
