+/**
+ * For deployment with a map of package dependency trees, generates
+ * Kernel config to be merged and interpolates parameter values into the configs
+ */
+@AllArgsConstructor
+public class ConfigResolver {
+
+    private final Kernel kernel;
+
+    private final Log log;
+
+    // Mapping of package name -> parameter name -> parameter value set in deployment
+    private final Map<String, Map<String, Parameter>> targetPackageConfigs;
+
+    // Mapping of top level package name -> resolved dependency tree
+    private final Map<String, Package> targetPackages;
+
+    private Set<String> removedTopLevelPackages;
+
+    private static final String REQUIRES_CONFIG_KEY = "requires";
+    private static final String VERSION_CONFIG_KEY = "version";
+    private static final String PARAMETER_REFERENCE_FORMAT = "{{params:%s.value}}";
+
+    /**
+     * Generates config to be merged with kernel
+     *
+     * @return kernel config to be merged
+     * @throws Exception
+     */
+    public Map<Object, Object> resolveConfig() throws Exception {
+        try {
+            Map<Object, Object> newConfig = new HashMap<>();
+
+            targetPackages.forEach((packageName, pkg) -> processPackage(newConfig, packageName, pkg));
+
+            newConfig.put(kernel.getMain().getName(), getUpdatedMainConfig(targetPackages));
+
+            return newConfig;
+        } catch (Throwable t) {
+            throw new Exception("Error resolving config", t);
+        }
+    }
+
+    /**
+     * Processes lifecycle section of each packaage and adds it to the config
+     *
+     * @param newConfig instance of config that services are to be added to
+     * @param packageName name of the package to generate config for
+     * @param pkg package object with package information
+     */
+    // TODO : Revisit after the Kernel config syntax is updated, DA currently does not understand the lifecycle syntax
+    private void processPackage(Map<Object, Object> newConfig, String packageName, Package pkg) {
+        Map<Object, Object> lifecycle = new HashMap<>();
+        lifecycle.putAll(pkg.getLifecycle());
+
+        // Interpolate parameters
+        Map<String, Parameter> parametersFromDeployment = targetPackageConfigs.get(packageName);
+        for (Map.Entry<Object, Object> lifecycleEntry : lifecycle.entrySet()) {
+            interpolate(lifecycleEntry.getKey(), lifecycle, parametersFromDeployment, pkg.getParameters());
+        }
+
+        // Generate requires list
+        Set<String> dependencyServiceNames = pkg.getDependencyPackageMap().values().stream().
+                map(dependency -> dependency.getServiceName()).collect(Collectors.toSet());
+        addServiceDependencies(lifecycle, dependencyServiceNames);
+
+        lifecycle.put(VERSION_CONFIG_KEY, pkg.getPackageVersion());
+        newConfig.put(pkg.getServiceName(), lifecycle);
+
+        // Process dependency packages
+        pkg.getDependencyPackageMap().forEach(
+                (dependencyPackageName, dependencyPackage) -> processPackage(newConfig, dependencyPackageName,
+                        dependencyPackage));
+    }
+
+    /**
+     * For lifecycle key-value pair of a package, substitutes parameter values
+     *
+     * @param lifecycleKey key of the key value pair in lifecycle map
+     * @param lifecycle lifecycle map
+     * @param parametersFromDeployment parameters set in deployment for package
+     * @param packageParameters all parameters configured for package
+     */
+    private void interpolate(Object lifecycleKey, Map<Object, Object> lifecycle,
+                             Map<String, Parameter> parametersFromDeployment,
+                             Set<Package.Parameter> packageParameters) {
+        if (lifecycle.get(lifecycleKey) instanceof String) {
+            String value = (String) lifecycle.get(lifecycleKey);
+
+            // Handle package parameters
+            for (final Package.Parameter parameter : packageParameters) {
+                String paramValue = parameter.getDefaultValue();
+                if (parametersFromDeployment != null && parametersFromDeployment.containsKey(parameter.getName())) {
+                    paramValue = parametersFromDeployment.get(parameter.getName()).getValue();
+                }
+                value = value.replace(String.format(PARAMETER_REFERENCE_FORMAT, parameter.getName()), paramValue);
+            }
+            lifecycle.put(lifecycleKey, value);
+
+            // TODO : Handle system parameters
+        } else {
+            Map<Object, Object> childLifecycleMap = (Map<Object, Object>) lifecycle.get(lifecycleKey);
+            for (Map.Entry<Object, Object> lifecycleEntry : childLifecycleMap.entrySet()) {
+                interpolate(lifecycleEntry.getKey(), childLifecycleMap, parametersFromDeployment, packageParameters);
+            }
+        }
+    }
+
+
+    /**
+     * Recompute main service dependencies for deployment
+     *
+     * @param targetPackages top level packages
+     * @return main service with updated dependencies
+     * @throws Throwable
+     */
+    private Map<Object, Object> getUpdatedMainConfig(Map<String, Package> targetPackages) throws Throwable {
+        Set<String> kernelDependencies =
+                kernel.getMain().getDependencies().keySet().stream().map(s -> s.getName()).collect(Collectors.toSet());
+        kernelDependencies.removeAll(removedTopLevelPackages);
+        kernelDependencies
+                .addAll(targetPackages.values().stream().map(p -> p.getServiceName()).collect(Collectors.toSet()));

