+package com.sap.cloud.lm.sl.cf.process.steps;
+
+import java.util.List;
+
+import javax.inject.Inject;
+
+import com.sap.cloud.lm.sl.cf.core.cf.metadata.processor.EnvMtaMetadataParser;
+import com.sap.cloud.lm.sl.cf.core.cf.metadata.processor.MtaMetadataParser;
+import com.sap.cloud.lm.sl.cf.process.util.HooksHelper;
+import com.sap.cloud.lm.sl.cf.process.util.HooksPhaseGetter;
+import com.sap.cloud.lm.sl.cf.process.util.HooksProcessor;
+import com.sap.cloud.lm.sl.cf.process.variables.Variables;
+import com.sap.cloud.lm.sl.mta.model.Hook;
+import com.sap.cloud.lm.sl.mta.model.Module;
+
+public abstract class TimeoutAsyncFlowableStepWithHooks extends TimeoutAsyncFlowableStep {
+
+    @Inject
+    private MtaMetadataParser mtaMetadataParser;
+    @Inject
+    private EnvMtaMetadataParser envMtaMetadataParser;
+    @Inject
+    private HooksPhaseGetter hooksPhaseGetter;
+    @Inject
+    private HooksProcessor hooksProcessor;
+
+    @Override
+    public StepPhase executeAsyncStep(ProcessContext context) {
+        HooksHelper hooksHelper = getHooksHelper(context);
+        StepPhase currentStepPhase = context.getVariable(Variables.STEP_PHASE);
+        Module moduleToDeploy = hooksHelper.determineModuleToDeploy(context);
+        List<Hook> executedHooks = hooksProcessor.executeBeforeStepHooks(hooksHelper, moduleToDeploy, currentStepPhase);
+        if (!executedHooks.isEmpty()) {
+            return currentStepPhase;
+        }
+        return executePollingStep(context);
+    }
+
+    protected HooksHelper getHooksHelper(ProcessContext context) {
+        return new HooksHelper.Builder().withContext(context)
+                                        .withMtaMetadataParser(mtaMetadataParser)
+                                        .withEnvMtaMetadataParser(envMtaMetadataParser)
+                                        .withHookPhasesBeforeStep(hooksPhaseGetter.getHookPhasesBeforeStop(this, context))
+                                        .withHookPhasesAfterStep(hooksPhaseGetter.getHookPhasesAfterStop(this, context))
+                                        .build();
+    }
+
+    protected abstract StepPhase executePollingStep(ProcessContext context);
+}
