+    }
+
+    public void setEnvMtaMetadataParser(EnvMtaMetadataParser envMtaMetadataParser) {
+        this.envMtaMetadataParser = envMtaMetadataParser;
+    }
+
+    public void setContext(ProcessContext context) {
+        this.context = context;
+    }
+
+    public void setHooksBeforeStepSupplier(Supplier<HookPhase> hooksBeforeStepSupplier) {
+        this.hooksBeforeStepSupplier = hooksBeforeStepSupplier;
+    }
+
+    public void setHooksAfterStepSupplier(Supplier<HookPhase> hooksAfterStepSupplier) {
+        this.hooksAfterStepSupplier = hooksAfterStepSupplier;
+    }
+
+    public static class Builder {
