+package net.fabricmc.loader.api;
+
+import java.util.Collections;
+import java.util.HashSet;
+import java.util.Objects;
+import java.util.Set;
+
+public final class VersionPredicate {
+	public enum Type {
+		ANY("*"),
+		EQUALS("="),
+		GREATER_THAN(">"),
+		LESSER_THAN("<"),
+		GREATER_THAN_OR_EQUAL(">="),
+		LESSER_THAN_OR_EQUAL("<="),
+		SAME_MAJOR("^"),
+		SAME_MAJOR_AND_MINOR("~");
+
+		private final String prefix;
+
+		Type(String prefix) {
+			this.prefix = prefix;
+		}
+
+		public String prefix() {
+			return prefix;
+		}
+
+		public String describe(String version) {
+			return this == ANY ? prefix : prefix + version;
+		}
+	}
+
+	private final Type type;
+	private final String version;
+
+	public VersionPredicate(Type type, String version) {
+		this.type = Objects.requireNonNull(type, "type == null!");
+		this.version = Objects.requireNonNull(version, "version == null!");
+	}
+
+	public Type getType() {
+		return type;
+	}
+
+	public String getVersion() {
+		return version;
+	}
+
+	@Override
+	public String toString() {
+		return type.describe(version);
+	}
+
+	@Override
+	public boolean equals(Object o) {
+		if (this == o) {
+			return true;
+		}
+
+		if (o == null || getClass() != o.getClass()) {
+			return false;
+		}
+
+		VersionPredicate that = (VersionPredicate) o;
+		return type == that.type && version.equals(that.version);
+	}
+
+	@Override
+	public int hashCode() {
+		return Objects.hash(type, version);
+	}
+
+	public static Set<VersionPredicate> parse(String... matchers) {
+		Set<VersionPredicate> predicates = new HashSet<>(matchers.length);
+
+		for (String matcher : matchers) {
+			char firstChar = matcher.charAt(0);
+			char secondChar = 0;
+
+			if (matcher.length() > 1) {
+				secondChar = matcher.charAt(1);
+			}
+
+			switch (firstChar) {
+			case '*':
+				if (matcher.length() == 1) {
+					predicates.clear();
+					predicates.add(new VersionPredicate(Type.ANY, ""));
+				} else {
+					predicates.add(new VersionPredicate(Type.EQUALS, matcher));
+				}

