+
+      @ProtoFactory
+      IgnoredCaches(Set<String> caches) {
+         // ProtoStream cannot use KeySetView directly as it does not have a zero args constructor
+         this.caches = ConcurrentHashMap.newKeySet(caches.size());
+         this.caches.addAll(caches);
+      }
+   }
