+   @Override
+   public void channelActive(ChannelHandlerContext ctx) throws Exception {
+      if (!processRules(ctx)) {
+         throw new IllegalStateException("cannot determine to accept or reject a channel: " + ctx.channel());
+      } else {
+         ctx.fireChannelActive();
+      }
+   }
