+        assertEquals(nbExpectedRenditions, transcodedVideos.size());
+        for (int i = 0; i < nbExpectedRenditions; i++) {
+            assertEquals(expectedRenditions.get(i), transcodedVideos.get(i).get("name"));
+        }
+    }
