+            Request request = prepareSOAPRequest(service, soapAction, arguments).timeout(SOAP_TIMEOUT,
+                    TimeUnit.MILLISECONDS);
+            request.getContent().forEach(buffer -> logger.trace("Request: {}", new String(buffer.array())));

