+        	DatabaseMetaData md = conn.getMetaData();
+        	ResultSet rs = md.getTables(null, null, "AutomaticDatabase", null);
+        	while(rs.next()) {
+        		isTableCreated = true;
+        		return;
+        	}
+    		
+    		//If not, create it.    		
+    		try (PreparedStatement pstmt= conn.prepareStatement(createTable)) {
+    			pstmt.executeUpdate();
+    		}
+    		isTableCreated = true;
+    	} catch (SQLException e) {
+			e.printStackTrace();

