+import com.google.common.base.Strings;
+
+public class FlowMapper {
+
+    public static Flow buildFlowFromProto(FlowMessage flowMessage) {
+        return new Flow() {
+            @Override
+            public long getTimestamp() {
+                return flowMessage.getTimestamp();
+            }
+
+            @Override
+            public Long getBytes() {
+                return flowMessage.hasNumBytes() ? flowMessage.getNumBytes().getValue() : null;
+            }
+
+            @Override
+            public Direction getDirection() {
+                switch (flowMessage.getDirection()) {
+                    case INGRESS:
+                        return Direction.INGRESS;
+                    case EGRESS:
+                        return Direction.EGRESS;
+                }
+                return Direction.INGRESS;
+
+            }
+
+            @Override
+            public String getDstAddr() {
+
+                if (!Strings.isNullOrEmpty(flowMessage.getDstAddress())) {
+                    return flowMessage.getDstAddress();
+                }
+                return null;
+            }
+
+            @Override
+            public Optional<String> getDstAddrHostname() {
+                if (!Strings.isNullOrEmpty(flowMessage.getDstHostname())) {
+                    return Optional.of(flowMessage.getDstHostname());
+                }
+                return Optional.empty();
+            }
+
+            @Override
+            public Long getDstAs() {
+                return flowMessage.hasDstAs() ? flowMessage.getDstAs().getValue() : null;
+            }
+
+            @Override
+            public Integer getDstMaskLen() {
+                return flowMessage.hasDstMaskLen() ? flowMessage.getDstMaskLen().getValue() : null;
+            }
+
+            @Override
+            public Integer getDstPort() {
+                return flowMessage.hasDstPort() ? flowMessage.getDstPort().getValue() : null;
+            }
+
+            @Override
+            public Integer getEngineId() {
+                return flowMessage.hasEngineId() ? flowMessage.getEngineId().getValue() : null;
+            }
+
+            @Override
+            public Integer getEngineType() {
+                return flowMessage.hasEngineType() ? flowMessage.getEngineType().getValue() : null;
+            }
+
+            @Override
+            public Long getDeltaSwitched() {
+                return flowMessage.hasDeltaSwitched() ? flowMessage.getDeltaSwitched().getValue() : null;
+            }
+
+            @Override
+            public Long getFirstSwitched() {
+                return flowMessage.hasFirstSwitched() ? flowMessage.getFirstSwitched().getValue() : null;
+            }
+
+            @Override
+            public int getFlowRecords() {
+                return flowMessage.hasNumFlowRecords() ? flowMessage.getNumFlowRecords().getValue() : 0;
+            }
+
+            @Override
+            public long getFlowSeqNum() {
+                return flowMessage.hasFlowSeqNum() ? flowMessage.getFlowSeqNum().getValue() : 0L;
+            }
+
+            @Override
+            public Integer getInputSnmp() {
+                return flowMessage.hasInputSnmpIfindex() ? flowMessage.getInputSnmpIfindex().getValue() : null;
+            }
+
+            @Override
+            public Integer getIpProtocolVersion() {
+                return flowMessage.hasIpProtocolVersion() ? flowMessage.getIpProtocolVersion().getValue() : null;
+            }
+
+            @Override
+            public Long getLastSwitched() {
+                return flowMessage.hasLastSwitched() ? flowMessage.getLastSwitched().getValue() : null;
+            }
+
+            @Override
+            public String getNextHop() {
+                if (!Strings.isNullOrEmpty(flowMessage.getNextHopAddress())) {
+                    return flowMessage.getNextHopAddress();
+                }
+                return null;
+            }
+
+            @Override
+            public Optional<String> getNextHopHostname() {
+                if (!Strings.isNullOrEmpty(flowMessage.getNextHopHostname())) {
+                    return Optional.of(flowMessage.getNextHopHostname());
+                }
+                return Optional.empty();
+            }
+
+            @Override
+            public Integer getOutputSnmp() {
+                return flowMessage.hasOutputSnmpIfindex() ? flowMessage.getOutputSnmpIfindex().getValue() : null;
+            }
+
+            @Override
+            public Long getPackets() {
+                return flowMessage.hasNumPackets() ? flowMessage.getNumPackets().getValue() : null;
+            }
+
+            @Override
+            public Integer getProtocol() {
+                return flowMessage.hasProtocol() ? flowMessage.getProtocol().getValue() : null;
+            }
+
+            @Override
+            public SamplingAlgorithm getSamplingAlgorithm() {
+
+                switch (flowMessage.getSamplingAlgorithm()) {
+                    case SystematicCountBasedSampling:
+                        return SamplingAlgorithm.SystematicCountBasedSampling;
+                    case SystematicTimeBasedSampling:
+                        return SamplingAlgorithm.SystematicTimeBasedSampling;
+                    case RandomNoutOfNSampling:
+                        return SamplingAlgorithm.RandomNoutOfNSampling;
+                    case UniformProbabilisticSampling:
+                        return SamplingAlgorithm.UniformProbabilisticSampling;
+                    case PropertyMatchFiltering:
+                        return SamplingAlgorithm.PropertyMatchFiltering;
+                    case HashBasedFiltering:
+                        return SamplingAlgorithm.HashBasedFiltering;
+                    case FlowStateDependentIntermediateFlowSelectionProcess:
+                        return SamplingAlgorithm.FlowStateDependentIntermediateFlowSelectionProcess;
+                }
+                return SamplingAlgorithm.Unassigned;
+            }
+
+            @Override
+            public Double getSamplingInterval() {
+                return  flowMessage.hasSamplingInterval() ? flowMessage.getSamplingInterval().getValue() : null;
+            }
+
+            @Override
+            public String getSrcAddr() {
+                if(!Strings.isNullOrEmpty(flowMessage.getSrcAddress())) {
+                    return flowMessage.getSrcAddress();
+                }
+                return null;
+            }
+
