+            public Optional<String> getSrcAddrHostname() {
+                if (!Strings.isNullOrEmpty(flowMessage.getSrcHostname())) {
+                    return Optional.of(flowMessage.getSrcHostname());
+                }
+                return Optional.empty();
+            }
+
+            @Override
+            public Long getSrcAs() {
+                return flowMessage.hasSrcAs() ? flowMessage.getSrcAs().getValue() : null;
+            }
+
+            @Override
+            public Integer getSrcMaskLen() {
+                return flowMessage.hasSrcMaskLen() ? flowMessage.getSrcMaskLen().getValue() : null;
+            }
+
+            @Override
+            public Integer getSrcPort() {
+                return flowMessage.hasSrcPort() ? flowMessage.getSrcPort().getValue() : null;
+            }
+
+            @Override
+            public Integer getTcpFlags() {
+                return flowMessage.hasTcpFlags() ? flowMessage.getTcpFlags().getValue() : null;
+            }
+
+            @Override
+            public Integer getTos() {
+                return flowMessage.hasTos() ? flowMessage.getTos().getValue() : null;
+            }
+
+            @Override
+            public NetflowVersion getNetflowVersion() {
+                switch (flowMessage.getNetflowVersion()) {
+                    case V5:
+                        return NetflowVersion.V5;
+                    case V9:
+                        return NetflowVersion.V9;
+                    case IPFIX:
+                        return NetflowVersion.IPFIX;
+                    case SFLOW:
+                        return NetflowVersion.SFLOW;
+                    default:
+                        return NetflowVersion.V5;

