+            this(cvDebugInfo, findObjectName(cvDebugInfo));
+        }
+
+        private static String findObjectName(CVDebugInfo cvDebugInfo) {
+            /* Get file from first class object */
+            String fn = null;
+            for (ClassEntry classEntry : cvDebugInfo.getPrimaryClasses()) {
+                if (classEntry.getFileName() != null) {
+                    fn = classEntry.getFileEntry().getFileName();
+                    if (fn.endsWith(".java")) {
+                        fn = fn.substring(0, fn.lastIndexOf(".java")) + ".obj";
+                    }
+                    break;
+                }
+            }
+            return fn;
+        }
+
+        boolean isValid() {
+            return objName != null;
+        }
+
+        @Override
+        protected int computeSize(int initialPos) {
+            int pos = initialPos + Integer.BYTES; /* signature = 0; */
+            pos += objName.getBytes(UTF_8).length + 1; /* inline null terminated */
+            return pos;
+        }
+
+        @Override
+        protected int computeContents(byte[] buffer, int initialPos) {
+            int pos = CVUtil.putInt(0, buffer, initialPos); /* signature = 0 */
