+    int computeFullContents(byte[] buffer, int initialPos) {
+        int pos = initialPos;
+        pos += Short.BYTES; /* save room for length (no including length bytes) */
+        pos = CVUtil.putShort(cmd, buffer, pos);
+        pos = computeContents(buffer, pos);
