+            cf2 = (byte) 0;
+            padding = (byte) 0;
+            machine = (short) 208;
+            feMajor = (short) 2;
+            feMinor = (short) 3;
+            feBuild = (short) 4;
+            feQFE = (short) 5;
+            beMajor = (short) 6;
+            beMinor = (short) 7;
+            beBuild = (short) 8;
+            beQFE = (short) 9;
+            compiler = "graal";
+        }
