+            new TypeReference<ExtPrebid<?, ExtImpInmobi>>() {
+            };
+
+    private final String endpointUrl;
+    private final JacksonMapper mapper;
+
+    public InmobiBidder(String endpointUrl, JacksonMapper mapper) {
