+package net.runelite.client.plugins.opponentinfo;
+
+import com.google.inject.Guice;
+import com.google.inject.Inject;
+import com.google.inject.testing.fieldbinder.Bind;
+import com.google.inject.testing.fieldbinder.BoundFieldModule;
+import net.runelite.api.*;
+import net.runelite.api.events.InteractingChanged;
+import net.runelite.client.ui.overlay.OverlayManager;
+import org.junit.Before;
+import org.junit.Test;
+import org.junit.runner.RunWith;
+import org.mockito.Mock;
+import org.mockito.junit.MockitoJUnitRunner;
+
+import static org.junit.Assert.*;
+import static org.mockito.Mockito.*;
+
+@RunWith(MockitoJUnitRunner.class)
+public class OpponentInfoPluginTest
+{
+    @Mock
+    @Bind
+    Client client;
+
+    @Mock
+    @Bind
+    OverlayManager overlayManager;
+
+    @Mock
+    @Bind
+    OpponentInfoConfig opponentInfoConfig;
+
+    @Mock
+    @Bind
+    OpponentInfoOverlay opponentInfoOverlay;
+
+    @Mock
+    @Bind
+    PlayerComparisonOverlay playerComparisonOverlay;
+
+    @Inject
+    OpponentInfoPlugin opponentInfoPlugin;
+
+    @Before
+    public void before()
+    {
+        Guice.createInjector(BoundFieldModule.of(this)).injectMembers(this);
+    }
+
+    /*
+     * Verify that the current opponent in multi is always the npc that the player is attacking
+     */
+    @Test
+    public void testAttackingEnemyInMulti() {
+        Player thePlayer = mock(Player.class), otherPlayer = mock(Player.class);
+        NPC aggro = mock(NPC.class), attacked = mock(NPC.class);
+        when(client.getLocalPlayer()).thenReturn(thePlayer);
+
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(thePlayer, null));
+
+        // verify that there is currently no opponent
+        assertNull(opponentInfoPlugin.getLastOpponent());
+
+        // some npc attacks the player
+        InteractingChanged aggroEnemy = new InteractingChanged(aggro, thePlayer);
+        opponentInfoPlugin.onInteractingChanged(aggroEnemy);
+
+        // verify that the current opponent is the aggressive npc
+        assertSame(opponentInfoPlugin.getLastOpponent(), aggro);
+
+        // the player attacks a different npc
+        when(thePlayer.getInteracting()).thenReturn(attacked);
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(thePlayer, attacked));
+        // the npc attacks them back
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(attacked, thePlayer));
+
+        // verify that the current opponent is now the attacked npc
+        assertSame(opponentInfoPlugin.getLastOpponent(), attacked);
+
+        // the player is attacked by an aggressive npc while attacking a different npc
+        opponentInfoPlugin.onInteractingChanged(aggroEnemy);
+
+        // verify that the current opponent is still the npc the player is attacking
+        assertSame(opponentInfoPlugin.getLastOpponent(), attacked);
+
+        // the npc attacking the player that is not the current opponent attacks a different player
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(aggro, otherPlayer));
+
+        // verify that the current opponent is still the npc the player is attacking
+        assertSame(opponentInfoPlugin.getLastOpponent(), attacked);
+
+        // the npc the player is attacking, the current opponent, attacks a different player
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(attacked, otherPlayer));
+
+        // verify that the current opponent is still the npc the player is attacking
+        assertSame(opponentInfoPlugin.getLastOpponent(), attacked);
+    }
+
+    /*
+     * Verify that the current opponent is the expected npc while the player is not attacking anything
+     */
+    @Test
+    public void testIdleInMulti() {
+        Player thePlayer = mock(Player.class), otherPlayer = mock(Player.class);
+        NPC aggroFirst = mock(NPC.class), aggroRecent = mock(NPC.class);
+        when(client.getLocalPlayer()).thenReturn(thePlayer);
+
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(thePlayer, null));
+
+        // verify that there is currently no opponent
+        assertNull(opponentInfoPlugin.getLastOpponent());
+
+        // some npc attacks the player
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(aggroFirst, thePlayer));
+
+        // verify that the current opponent is the aggressive npc
+        assertSame(opponentInfoPlugin.getLastOpponent(), aggroFirst);
+
+        // the player is attacked by a different npc
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(aggroRecent, thePlayer));
+
+        // verify that the current opponent is the most recent aggressor
+        assertSame(opponentInfoPlugin.getLastOpponent(), aggroRecent);
+
+        // an npc that is not the current opponent targets another player
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(aggroFirst, otherPlayer));
+
+        // verify that the current opponent is still the most recent aggressor
+        assertSame(opponentInfoPlugin.getLastOpponent(), aggroRecent);
+
+        // the current opponent switches targets to another player
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(aggroRecent, otherPlayer));
+
+        // verify that there is no longer an opponent
+        // (if lastTime is not null, then the lastOpponent will become null after OpponentInfoPlugin.WAIT time)
+        assertTrue(opponentInfoPlugin.getLastTime() != null || opponentInfoPlugin.getLastOpponent() == null);
+    }
+
+    /*
+     * Verify that the current opponent is the expected npc in singles
