+        assertTrue(opponentInfoPlugin.getLastTime() != null || opponentInfoPlugin.getLastOpponent() == null);
+
+        // the player attacks the npc
+        when(thePlayer.getInteracting()).thenReturn(playerNPC);
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(thePlayer, playerNPC));
+        // the npc attacks them back
+        when(playerNPC.getInteracting()).thenReturn(thePlayer);
+        opponentInfoPlugin.onInteractingChanged(new InteractingChanged(playerNPC, thePlayer));

