+
+		IEditorInput original = findOriginalSourceForOuputFolderCopy(input);
+		if (original != input) {
+			return original;
+		}
+
+		IEclipseTrace trace = traceInformation.getTraceToSource(resource);
+		if (trace == null) {
+			return input;
+		}
+
+		Optional<? extends ILocationInEclipseResource> sourceInformation = Iterables.tryFind(trace.getAllAssociatedLocations(),
+				(candidate) -> languageInfo.equals(candidate.getLanguage()));
+
+		if (!sourceInformation.isPresent()) {
+			return input;
+		}
+
+		IStorage originalStorage = sourceInformation.get().getPlatformResource();
+		if (originalStorage != null) {
+			return EditorUtils.createEditorInput(originalStorage);
+		}
+		
+		return null;
+	}
+
