+				for (BeforeBreakBlockCallback event : listeners) {
+					ActionResult result = event.beforeBlockBreak(player, pos, state, entity, block);
+
+					if (result != ActionResult.FAIL) {
+						return result;
+					}
+				}
+
+				return ActionResult.FAIL;
+			}
